public String getAttributeRequestValue(AccountRequest acctReq, String attribute) {
    if (acctReq != null) {
        AttributeRequest attrReq = acctReq.getAttributeRequest(attribute);
        if (attrReq != null) {
            return attrReq.getValue();
        }
    }
    return null;
