import sailpoint.connector.DelimitedFileConnector;

     Map map = DelimitedFileConnector.defaultBuildMap( cols, record );
     String surrogateaccess = (String) map.get( "surrogateaccess" );
	 String personpermissiongrants = (String) map.get( "personpermissiongrants" );
	 
	 if (surrogateaccess != null) {
	 String[] surrogateaccessArray = surrogateaccess.split(",");
     
	 List surrogateaccessList = Arrays.asList(surrogateaccessArray);
	 map.put("surrogateaccess", surrogateaccessList);
  
     }
	 
	 if (personpermissiongrants != null) {
	 String[] personpermissiongrantsArray = personpermissiongrants.split(",");
     
	 List personpermissiongrantsList = Arrays.asList(personpermissiongrantsArray);
	 map.put("personpermissiongrants", personpermissiongrantsList);
	 
     }
	 
     return map;
