import sailpoint.connector.*;
	 
	 String objType = schema.getObjectType();
	 Map map = JDBCConnector.buildMapFromResultSet(result, schema);
	 if (objType.equals("account")){
       
            String isActive = (String) map.get("IsActive");
            if(isActive.equals("Y")) {
                map.put("IIQDisabled", false);
            } else {
                map.put("IIQDisabled", true);
            }
			}
     return map;
