import sailpoint.connector.DelimitedFileConnector;

     Map map = DelimitedFileConnector.defaultBuildMap(cols, record);
     String deltaAdjuster = (String) map.get("Delta_Adjuster");
	 String canCreate = (String) map.get("Can_Create");
	 String userAdmin = (String) map.get("User_Admin");
	 String canViewAllCases = (String) map.get("Can_View_All_Cases");
	 List permList = new ArrayList();
	 
	 if (deltaAdjuster != null && deltaAdjuster.equals("Y")) {
	 permList.add("Delta_Adjuster");
     }
	 
	 if (canCreate != null && canCreate.equals("Y")) {
	 permList.add("Can_Create");
     }
	 
	 if (userAdmin != null && userAdmin.equals("Y")) {
	 permList.add("User_Admin");
     }
	 
	 if (canViewAllCases != null && canViewAllCases.equals("Y")) {
	 permList.add("Can_View_All_Cases");
     }
	 
	 map.put("permissions", permList);
	 
     return map;
