import sailpoint.connector.DelimitedFileConnector;
 
    Map map = DelimitedFileConnector.defaultBuildMap(cols, record);
    String hasXactContents = (String) map.get("Has XactContents");
         String hasTimeandMaterials = (String) map.get("Has Time and Materials");
         String isAdmin = (String) map.get("Is Admin");
         List permList = new ArrayList();
		 String securityGroups = (String) map.get("Security Groups");
	 
	 
	 if (securityGroups != null) {
	   String[] securityGroupsArray = securityGroups.split(",");
     
	   List securityGroupsList = Arrays.asList(securityGroupsArray);
	   map.put("Security Groups", securityGroupsList);
  
     }
         
         if (hasXactContents != null && hasXactContents.equals("yes")) {
         permList.add("Has XactContents");
    }
         
         if (hasTimeandMaterials != null && hasTimeandMaterials.equals("yes")) {
         permList.add("Has Time and Materials");
    }
         
         if (isAdmin != null && isAdmin.equals("yes")) {
         permList.add("Is Admin");
    }
         
         map.put("permissions", permList);
         
    return map;
